package org.muxun.algorithm.leetcode.java.string;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

/**
 * @author 慕勋
 * @created 2020-03-12
 */
@DisplayName("字符串的最大公因子测试类")
public class GcdOfStringsTest {

    @Test
    public void gcdOfStringsTestOne() {
        Assertions.assertEquals("ABC", new GcdOfStrings().gcdOfStrings("ABCABC", "ABC"));
    }

    @Test
    public void gcdOfStringsTestTwo() {
        Assertions.assertEquals("AB", new GcdOfStrings().gcdOfStrings("ABABAB", "ABAB"));
    }

    @Test
    public void gcdOfStringsTestThree() {
        Assertions.assertEquals("", new GcdOfStrings().gcdOfStrings("LEET", "CODE"));
    }

    @Test
    public void gcdOfStringsTestFour() {
        Assertions.assertEquals("ABAB", new GcdOfStrings().gcdOfStrings("ABABABAB", "ABAB"));
    }

    @Test
    public void gcdOfStringsTestFive() {
        Assertions.assertEquals("", new GcdOfStrings().gcdOfStrings("", ""));
    }
}
