package org.muxun.algorithm.leetcode.java.string;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

/**
 * @author 慕勋
 * @created 2020-03-19
 */
@DisplayName("最长回文字符串单元测试")
public class TheLongestPalindromeTest {

    @Test
    public void longestPalindromeTestOne() {
        Assertions.assertEquals(7, new TheLongestPalindrome().longestPalindrome("abccccdd"));
    }

    @Test
    public void longestPalindromeTestTwo() {
        Assertions.assertEquals(983, new TheLongestPalindrome().longestPalindrome(""));
    }
}
